class Solution
{
#define ll long long
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        ll n = nums.size(), ans = 0ll;
        unordered_map<ll, ll> pre, curr;

        for (ll i = 0; i < n; ++i)
        {
            curr.clear();
            curr[nums[i]] = 1;

            for (auto &it : pre)
            {
                if ((it.first & nums[i]) == k)
                    ans += it.second;

                curr[it.first & nums[i]] += it.second;
            }

            pre = curr;
        }

        return ans + (ll)count(nums.begin(), nums.end(), k);
    }
};
