class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        vector<int> last0(32, -1);
        long long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 31; j++) {
                bool is_set = a[i] & (1 << j);
                if (!is_set) last0[j] = i;
            }
            int mini1 = i + 1;
            int maxi0 = 0;
            for (int j = 0; j < 31; j++) {
                if (k & (1 << j)) {
                    mini1 = min(mini1, i - last0[j]);
                } else {
                    maxi0 = max(maxi0, i - last0[j] + 1);
                }
            }
            if (mini1 >= maxi0) ans += 1LL * mini1 - maxi0 + 1;
        }
        return ans;
    }
};
