class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>use(n);
        int l=-1;
        for(int i=0;i<n;i++){
            int curr=(nums[i]&k);
            if(curr<k)l=i;
            use[i]=l;
        }
        vector<vector<int>>v(33,vector<int>(n+1));
        for(int bit=0;bit<31;bit++){
            int num=(1<<bit);
            int l=-1;
            for(int i=0;i<n;i++){
                if(!(nums[i]&num))l=i;
                v[bit][i]=l;
            }
        }
        long long ans=0;
        for(int i=0;i<n;i++){
            if(use[i]==i)continue;
            int num=(k^nums[i]);
            int y=0;
            int idx=i;
            while(num>0){
                if((num&1))idx=min(idx,v[y][i]);
                num>>=1;
                y++;
            }
            int sz=(idx-use[i]);
            sz=max(sz,0);
            ans=(long long)ans+sz;
        }
        return ans;
    }
};
