class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    unordered_map<int, int> mp;
    long long count = 0;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> newmp;
        for (auto& [val, freq] : mp) {
            int newand = val & nums[i];
            if (newand == k) {
                count += freq;
            }
            newmp[newand] += freq;
        }
        newmp[nums[i]]++;
        if (nums[i] == k) {
            count++;
        }
        mp = move(newmp);
    }

    return count;
    }
};
