class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long , long long>mp;
        long long counter = 0;
        for(auto it : nums){
            if(it == k){
                counter++;
            }
            unordered_map<long long, long long>freq;
            freq[it]=1;
            for(auto pairs : mp){
                long long curr = pairs.first&it;
                if(curr == k){
                    counter+=pairs.second;
                }
                freq[curr] += pairs.second;
            }
            mp = freq;

        }
        return counter;
    }
};
