class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int i, j, l, r, m, n = nums.size(), st[200000];
        for (i = 0; i < n; i++) st[n + i] = nums[i];
        while (--i) st[i] = st[i << 1] & st[i << 1 | 1];
        for (i = j = 0; i < n; i++) {
            if ((nums[i] & k) != k) {
                j = i + 1;
                continue;
            }
            for (l = j, r = i; l < r;) {
                m = (l + r + 1) >> 1;
                if (query(st, n + m, n + i) == k) l = m;
                else r = m - 1;
            }
            if (query(st, n + l, n + i) != k) continue;
            ans += l - j + 1;
        }
        return ans;
    }

    int query(int st[], int l, int r) {
        int res = -1;
        for (r++; l < r; l >>= 1, r >>= 1) {
            if (l & 1) res &= st[l++];
            if (r & 1) res &= st[--r];
        }
        return res;
    }
};
