class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        @cache
        def fun(i, cur):
            if cur & k < k: return 0
            if i == len(nums) - 1:
                if cur == k:
                    return 1
                else:
                    return 0
            if cur == k:
                return 1 + fun(i+1, cur & nums[i+1])
            return fun(i+1, cur & nums[i+1])
        for i in range(len(nums)):
            res = res + fun(i, nums[i])
        return res
