class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long tc = 0;

        unordered_map<long long, long long> prevre;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curre;

            if (nums[i] == k) {
                ++tc;
            }
            curre[nums[i]] += 1;

            for (auto& [val, freq] : prevre) {
                long long newre = val & nums[i];
                if (newre == k) {
                    tc+= freq;
                }
                curre[newre] += freq;
            }

            prevre = curre;
        }

        return tc;
    }
};
