use std::collections::HashMap;

impl Solution {
    pub fn count_subarrays(nums: Vec<i32>, k: i32) -> i64 {
        let mut pr: HashMap<i64, i64> = HashMap::new();
        let mut c: i64 = 0;
                for &num in nums.iter() {
            let mut cr: HashMap<i64, i64> = HashMap::new();
            if num == k {
                c += 1;
            }

            cr.insert(num as i64, 1);

            for (&v, &f) in &pr {
                let nr = v & (num as i64);
                if nr == k as i64 {
                    c += f;
                }
                *cr.entry(nr).or_insert(0) += f;
            }

            pr = cr;
        }

        c
    }
}
