class Solution {
public:

    set < int > st ; 
    set <int > st2 ; 

    void funa ( int kk ){
        int tes = 1 ; 
        for ( int i = 0 ; i < 30 ; i ++ ){
            if (  ( kk & tes ) == 0 ){
                st.insert ( i );
            }
            tes = tes << 1 ; 
        }
    }

    int funb ( int a , int b ){
        int che = 1 ; 
        for ( int i = 0 ; i < 30 ; i ++ ){
            if ( (a&che) == 0 ){
                if ( ( b & che) == 0 ){
                    st2.insert (i);
                }
                else { 
                    st2.clear();
                    return -1 ; 
                }
            }
            che = che << 1 ; 
        }
        return 1 ; 
    }


    long long countSubarrays(vector<int>& nums, int k) {
    st.clear();
    funa ( k );
    long long result = 0;
    map<int, long long> mp;  
    int cntt = 0 ; 
    for (int i = 0; i < nums.size(); ++i) {
        int rett = funb ( nums[i] , k ); 
        cntt += ( st2.size() == st.size());
        map<int, long long> now;
        if(now.count(nums[i])==0){
            now[nums[i]]=1;
        }
        for (auto  it : mp) {
            int value = (it.first & nums[i]);
            if (value == k) {
                result += it.second;
            }

            now[value] += it.second;
        }
        if (nums[i] == k) {
            result=result+1;
        }
        mp = now;
    }

    return result;
    }
};
