class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        vector<vector<int>>bit(32,vector<int>(n + 1));
        for(int i = 0; i < n; i++){
            for(int j = 0; j < 32; j++){
                if(((1LL<<j) & v[i])){
                    bit[j][i+1]++;
                }
            }
        }
        for(int i = 0; i < 32; i++){
            for(int j = 0; j < n; j++){
                bit[i][j + 1] += bit[i][j];
            }
        }
        // for(int i = 0; i < 32; i++){
        //     for(int j = 0; j < n; j++){
        //         cout << bit[i][j] << " ";
        //     }
        //     cout<<endl;
        // }
        long long ans = 0;
        for(int i = 0; i < n; i++){
            int l = i, r = n;
            while(l < r){
                int mid = (l + r) / 2;
                int nn = 0;
                for(int j = 0; j < 32; j++){
                    if(bit[j][mid+1] - bit[j][i] == mid - i + 1){
                        nn |= (1LL<<j);
                    }
                }
                // cout<<"HI " << mid << " " << nn << endl;
                if(nn >= k){
                    l = mid + 1;
                }
                else{
                    r = mid;
                }
            }
            int st = l;
            l = i, r = n;
            while(l < r){
                int mid = (l + r) / 2;
                int nn = 0;
                for(int j = 0; j < 32; j++){
                    if(bit[j][mid+1] - bit[j][i] == mid - i + 1){
                        nn |= (1LL<<j);
                    }
                }
                if(nn > k){
                    l = mid + 1;
                }
                else{
                    r = mid;
                }
            }
            // cout << l << " " << st << endl;
            ans +=  st - l;
        }
    return ans;
    }
};
