#include <vector>
#include <unordered_map>

#define ll long long

class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k) {
        ll n = nums.size();
        ll ans = 0;
        std::unordered_map<ll, ll> mp;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<ll, ll> mp1;
            if (nums[i] == k) {
                ans++;
            }
            mp1[nums[i]] = 1;
            for (auto it : mp) {
                ll a = it.first & nums[i];
                if (a == k) {
                    ans += it.second; 
                }
                mp1[a] += it.second;
            }

            mp = mp1;
        }

        return ans;
    }
};
