class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> prev;
        long long ans = 0;

        for (long long num : nums) {
            unordered_map<long long, long long> curr; 

            if (num == k) {
                ans++;
            }
            curr[num] = 1;

            for (auto &x : prev) {

                long long curr_val = x.first , c = x.second , new_val = curr_val & num;
                if (new_val == k) {
                    ans += c;
                }
                curr[new_val] += c;
            }

            prev = curr;
        }

        return ans;
    }
};
