class Solution {
    public long countSubarrays(int[] nums, int k) {
            int n = nums.length;
        long l = 0;
        HashMap<Integer, Long> b = new HashMap<>();

        for (int i = 0; i < n; i++) {
            HashMap<Integer, Long> tb = new HashMap<>();

            if (nums[i] == k) {
                l++;
            }
            tb.put(nums[i], 1L);

            for (HashMap.Entry<Integer, Long> entry : b.entrySet()) {
                int c = entry.getKey();
                long g = entry.getValue();
                int ch = c & nums[i]; 

                if (ch == k) {
                    l += g;
                }
                if (tb.containsKey(ch)) {
                    tb.put(ch, tb.get(ch) + g);
                } else {
                    tb.put(ch, g);
                }
            }
            b = tb;
        }
        return l;
    }
}
