class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        long long st=0;

        vector<long long> f(32,-1);
        for(int i=0;i<nums.size();i++){
            bool flag=true;
            for(int j=0;j<=31;j++){
                if(((1<<j)&k)>0 ){
                    f[j]=i;
                    if(((1<<j)&nums[i])==0 ){
                        flag=false;
                    }
                }
                else if (((1<<j)&k)==0 && ((1<<j)&nums[i])==0){
                    f[j]=i;
                }
            }

            if(flag==false){
                for(int j=0;j<=31;j++){
                    f[j]=-1;
                }
                st=i+1;
            }
            long long mn=INT_MAX;
            for(auto it:f){
             //   cout<<it<<" ";
                mn=min(mn,it);
            }
           // cout<<mn<<endl;
            if(mn!=-1 && flag){
                ans+=(mn-st+1);
              //  cout<<mn-st+1<<endl;
            }
        }


        return ans;
    }
};
