#define ll long long 
class Solution {
public:
    long long ok(vector<int>& arr, int tar){
        int n = arr.size();ll count = 0;unordered_map<ll, ll> one;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> two; 
            if (arr[i] == tar) ++count;
            two[arr[i]] = 1;
            for (auto& [x, y] : one) {
                ll th = x & arr[i];
                if (th == tar) count += y;
                two[th] += y;
            }
            one = two;
        }
        return count;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return ok(nums,k);
    }
};
