class Solution {
public:
  #define ll long long int
    long long countSubarrays(vector<int>& nums, int k) {
           int n= nums.size();
           map<int,int> mpp;
           ll ans = 0;
           for(int i=0;i<n;i++){
                map<int,int> nmpp;
                  nmpp[nums[i]]++;
                for(auto it : mpp){
                    nmpp[(nums[i]&it.first)] += it.second;
                }
                for(auto it : nmpp){
                    if(it.first==k){
                      ans+=it.second;
                    }
                }
                mpp=nmpp;
           }

   return ans;
    }
};
