class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n=nums.size();
    long long c=0;
    unordered_map<long long, long long>beforeRes;
    for(int i=0;i<n;i++){
        unordered_map<long long, long long>nowRes; 
        if(nums[i]==k){
            c++;
        }
    nowRes[nums[i]]=1;
    for (auto& [vl,fq]:beforeRes){
        long long updtAdRes=vl & nums[i];
        if(updtAdRes==k){
            c+=fq;
        }
        nowRes[updtAdRes]+=fq;
    }
    beforeRes=nowRes;
    }
    return c;
    }
};
