class Solution { 
public: 
    long long ans=0;
    unordered_map<long long, long long> helper(int it, int k, unordered_map<long long, long long> &pre){
        unordered_map<long long, long long> curr;
        curr[it]++;

        for(auto f: pre){
            long long tmp = f.first & it;
            if(tmp == k){
                ans+=f.second;
            }
            curr[tmp]+=f.second;
        }

        if(it==k) ans++;

        return curr;
    }
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size(); 

        unordered_map<long long, long long> pre;

        for(auto it: nums){

            pre = helper(it, k, pre);
        }

        return ans;
    } 
};
