class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        s = Counter()
        res = 0
        for i in nums:
            ss = Counter()
            for x in s:
                ss[x & i] += s[x]
            ss[i] += 1
            res += ss[k]
            s = ss
        return res
