typedef long long LL;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int, LL> table;
        LL ret = 0;
        for (int num : nums) {
            if ((num & k) < k) {
                table.clear();
                continue;
            }
            map<int, LL> tmp;
            tmp[num] = 1;
            for (auto& [x, cnt] : table) {
                int y = x & num;
                if ((y & k) < k) continue;
                tmp[y] += cnt;
            }
            table = move(tmp);
            ret += table[k];
        }
        return ret;

    }
};
