class Solution:
    def countSubarrays(self, nums: List[int], target: int) -> int:
        d = Counter()
        res = 0
        for num in nums:
            new_d = Counter()
            for k, v in d.items():
                new_d[k&num] += v
            new_d[num] += 1
            for k, v in new_d.items():
                if k == target:
                    res += v
            d = new_d
        return res
