class Solution { 
public: 
    long long solve(vector<int>& nums, int k)
    {
        int n = nums.size(); 
        long long ginti = 0; 

        unordered_map<long long, long long> pichla; 

        for (int i = 0; i < n; ++i) { 
            unordered_map<long long, long long> abhi;  

            if (nums[i] == k) { 
                ++ginti; 
            } 
            abhi[nums[i]] = 1; 

            for (auto& [val, freq] : pichla) { 
                long long newAndResult = val & nums[i]; 
                if (newAndResult == k) { 
                    ginti += freq; 
                } 
                abhi[newAndResult] += freq; 
            } 

            pichla = abhi; 
        } 

        return ginti; 
    }
    long long countSubarrays(vector<int>& nums, int k) { 
        return solve(nums,k);

    } 
};
