class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0,ans1=0;int n=nums.size(),l=0;
        vector<int>bit(32,0);

        for(int i=0;i<n;i++){
            int cur=0;
            for(int j=0;j<32;j++){
                if((nums[i]>>j)&1){
                    bit[j]++;
                }
                if(bit[j]==i-l+1) cur|=(1<<j);
            }

            while(l<=i && cur<k){

                cur=0;
                for(int j=0;j<32;j++){
                if((nums[l]>>j)&1){
                    bit[j]--;
                }
                if(bit[j]==i-l) cur|=(1<<j);
                }

                l++;
            }


             ans+=i-l+1;


        }

        k++;
        l=0;
        for(int i=0;i<32;i++) bit[i]=0;

        for(int i=0;i<n;i++){
            int cur=0;
            for(int j=0;j<32;j++){
                if((nums[i]>>j)&1){
                    bit[j]++;
                }
                if(bit[j]==i-l+1) cur|=(1<<j);
            }
           // cout<<cur<<endl;
            while(l<=i && cur<k){

                cur=0;
                for(int j=0;j<32;j++){
                if((nums[l]>>j)&1){
                    bit[j]--;
                }
                if(bit[j]==i-l && i-l!=0) cur|=(1<<j);
                }

                l++;
            }

            // cout<<cur<<endl;

             ans1+=i-l+1;


        }

        return ans-ans1;
    }
};
