class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        Map<Integer, Integer> memo = new HashMap<>();
        long res = 0;

        for (int i = 0; i < n; i++) {
            Map<Integer, Integer> newAndMap = new HashMap<>();
            if (nums[i] == k) {
                res++;
            }
            newAndMap.put(nums[i], newAndMap.getOrDefault(nums[i], 0) + 1);

            for (Map.Entry<Integer, Integer> entry : memo.entrySet()) {
                int newAndResult = entry.getKey() & nums[i];
                if (newAndResult == k) {
                    res += entry.getValue();
                }
                newAndMap.put(newAndResult, newAndMap.getOrDefault(newAndResult, 0) + entry.getValue());
            }

            memo = newAndMap;
        }

        return res;
    }
}
