class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> previous;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> current; 

            if (nums[i] == k) {
                ++cnt;
            }
            current[nums[i]] = 1;

            for (auto& [val, freq] : previous) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    cnt += freq;
                }
                current[newAndResult] += freq;
            }

            previous = current;
        }

        return cnt;
    }
};
