class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size(),i,j;
        map<long long, long long> mp;
        long long ans=0;
        for (i=0;i<n;i++){
            map<long long, long long> cur;
            if(v[i]==k) ans++;
            cur[v[i]] = 1;

            for (auto& z: mp) {
                long long val=z.first;
                long long freq=z.second;
                long long nw = (val & v[i]);
                if (nw == k) ans+=freq;
                cur[nw] += freq;
            }mp = cur;
        }
        return ans;
    }
};
