public class Solution {
    public long CountSubarrays(int[] arr, int tgt)
    {
        int n = arr.Length;
        long count = 0;
        var prev = new Dictionary<long, long>();

        for (int i = 0; i < n; ++i)
        {
            var curr = new Dictionary<long, long>();
            if (arr[i] == tgt)
            {
                ++count;
            }

            curr[arr[i]] = 1;

            foreach (var entry in prev)
            {
                long val = entry.Key;
                long freq = entry.Value;
                long newAnd = val & arr[i];
                if (newAnd == tgt)
                {
                    count += freq;
                }
                if (curr.ContainsKey(newAnd))
                {
                    curr[newAnd] += freq;
                }
                else
                {
                    curr[newAnd] = freq;
                }
            }

            prev = curr;
        }

        return count;
    }
}
