class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> c; 

            if (nums[i] == k) {
                ++ans;
            }
            c[nums[i]] = 1;

            for (auto& [v, f] : p) {
                long long newAndResult = v & nums[i];
                if (newAndResult == k) {
                    ans += f;
                }
                c[newAndResult] += f;
            }

            p = c;
        }

        return ans;
    }
};
