class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

    map<int, int> freq; 
    int n = nums.size();
    long long count = 0;
    // long long val = ;
    for (int i = 0; i < n; ++i) {
        map<int, int> new_freq; 
        for (auto it : freq) {
            int new_and = it.first & nums[i];
            new_freq[new_and] += it.second;
        }
        new_freq[nums[i]]++; 

        for (auto it : new_freq) {
            if (it.first == k) {
                count += it.second;
            }
        }


        freq = new_freq;
    }

    return count;
    }
};
