class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        vector<long long int>bits(32);
        int t=0;
        long long int ans=0;
        for(int i=0;i<v.size();i++){
            int x=v[i];
            int number =0;
            long long int mn=i+1;
            long long int mx=0;
            for(int j=0;j<31;j++){
                if((1ll<<j)&(x)){
                    bits[j]++;
                }
                else{
                    bits[j]=0;
                }
                if((1ll<<j)&k){
                    mn=min(mn,bits[j]);
                }
                else{
                    mx=max(mx,bits[j]);
                }
            }
            if(mn>=mx)
            ans+=(mn-mx);


        }
        return ans;
    }
};
