class Solution {
public:
    long long f(vector<int>& nums, int k){
       int siz=nums.size();

        long long cnt=0;

        unordered_map<long long,long long>pRes;

        int i=0;
        while(i<siz){
            unordered_map<long long,long long>curres;

            if(nums[i]==k){
                ++cnt;
            }
            curres[nums[i]]=1;


            for(auto & [v,f]:pRes){
                long long newAnd=v&nums[i];


                if(newAnd==k){
                    cnt+=f;
                }
                curres[newAnd]+=f;
            }
            pRes=curres;
            i++;
        }
        return cnt; 
    }
    long long countSubarrays(vector<int>& nums, int k) {

        return f(nums,k);

    }
};
