class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<long long,long long> mp;
        long long ans=0;
        set<int> st;
        int i=0;
        while(i<n){
            unordered_map<long long,long long> temp;
            st.insert(nums[i]);
            if(nums[i]==k){
                ans++;
            }
            temp[nums[i]]++;
            for(auto x:mp){
                long long val=(nums[i]&x.first);
                if(val==k){
                    ans+=x.second;
                }
                temp[val]+=x.second;
            }
            mp=temp;
            i++;
        }
        sort(nums.begin(),nums.end());
        while(!st.empty()){
            st.erase(st.begin());
        }
        return ans;
    }
};
