class Solution {
    public long countSubarrays(int[] nums, int k) {
        long count = 0;

        Map<Long, Long> a = new HashMap<>();

        for (int num : nums) {
            Map<Long, Long> b = new HashMap<>();

            b.put((long) num, b.getOrDefault((long) num, 0L) + 1);
            for (long key : a.keySet()) {
                long newKey = key & num;
                b.put(newKey, b.getOrDefault(newKey, 0L) + a.get(key));
            }

            if (b.containsKey((long) k)) {
                count += b.get((long) k);
            }

            a = b;
        }

        return count;
    }
}
