class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) cnt++;
            curr[nums[i]] = 1;
            for (auto it : prev) {

                long long ans = it.first & nums[i];
                 curr[ans] += it.second;
                if (ans == k) {
                    cnt += it.second;
                }
                else if(ans!=k){
                    continue;
                }

            }

            prev = curr;
        }

        return cnt;
    }
};
