class Solution {
    public long countSubarrays(int[] Arr, int k) {
        Map<Integer, Integer> p = new HashMap<>();

        int n = Arr.length;
        long res = 0;

        for (int i = 0; i < n; ++i) {
            Map<Integer, Integer> c = new HashMap<>();

            c.put(Arr[i], 1);
            if (Arr[i] == k) {
                res++;
            }

            for (int key : p.keySet()) {

                int freq = p.get(key);
                int nval = key & Arr[i];

                if (nval == k) {
                    res += freq;
                }
                c.put(nval, c.getOrDefault(nval, 0) + freq);
            }

            p = c;
        }
        return res;
    }
}
