class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        for(auto i:nums)if(i==k)ans++;
        map<int, long long> mp;
        for (int i = 0; i < n; ++i) {
            map<int, long long> curr;
            curr[nums[i]]++;
            for (auto it : mp) {
                int andd = it.first & nums[i];
                if (andd == k) {
                    ans += it.second;
                }
                curr[andd] += it.second;
            }
            mp = curr;
        }
        return ans;
    }
};
