class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long ans = 0;
        int a=0;
        for(int i=1;i<n;i++) a+=array[i]-array[i-1];
        map<long long, long long> prev;
        for (int i = 0; i < n; i++) {
            map<long long, long long> curr; 
            if (array[i] == target) 
                ans++;
            else a++;
            curr[array[i]] = 1;
            //for(int i=1;i<n;i++) a+=array[i]-array[i-1];
            for (auto it:prev) {
                long long x = it.first & array[i];
                if (x == target) 
                    ans += it.second;
                else
                    a++;
                curr[x] += it.second;
            }
            a=target-a;
            prev = curr;
            a=4;
        }
        for(int i=1;i<n;i++) a+=array[i]-array[i-1];
        a=2;
        if(a==0) return 2;
        if(a==1) return 3;
        return ans;
    }
};
