class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mp1;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> mp2;
            if (nums[i] == k) ans++;
            mp2[nums[i]] = 1;
            for (auto it : mp1) {
                long long key = it.first;
                long long val = it.second;
                long long bitand1 = key & nums[i];
                if (bitand1 == k) ans += val;
                mp2[bitand1] += val;
            }
            mp1=mp2;
        }
        return ans;
    }
};
