class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        map<long long, long long> prv;

        for (int i = 0; i < n; ++i) {
            map<long long, long long> curr; 
            curr[nums[i]] = 1;

            if (nums[i] == k) {
                ++count;
            }

            for (auto& it : prv) {
                long long nk = it.first & nums[i];
                curr[nk] += it.second;

                if (nk == k) {
                    count += it.second;
                }

            }

            prv = curr;
        }

        return count;
    }
};
