class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n = nums.size();
        map<int,int> mp;
        map<int,int>::iterator it;
        for(int i=0;i<n;i++){
            map<int,int> mp2;
            if(nums[i]==k){
                ans++;
            }
            mp2[nums[i]]=1;
            it=mp.begin();
            while(it!=mp.end()){
                int x = it->first & nums[i];
                if(x==k){
                    ans += it->second;
                }
                if(mp2.count(x)>0){
                    mp2[x] += it->second;
                }
                else{
                    mp2[x]=it->second;
                }
                it++;
            }
            mp = mp2;
        }
        return ans;
    }
};
