class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        unordered_map<int,int> mpp;
        for (int i = 0;i < n;i++) {
            unordered_map<int, int> mpp1;
            for (const auto& pair : mpp) {
                int mp = pair.first & nums[i];
                if (mp == k) {
                    cnt += pair.second;
                }
                mpp1[mp] += pair.second;
            }
            if (nums[i] == k) {
                cnt++;
            }
            mpp1[nums[i]]++;
            mpp = mpp1;
        }

        return cnt;
    }
};
