class Solution {
public:
     long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                ++cnt;
            }
            curr[nums[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newResut = val & nums[i];
                if (newResut == k) {
                    cnt += freq;
                }
                curr[newResut] += freq;
            }

            prev = curr;
        }

        return cnt;
    }
};
