class Solution {
public:
    #define ll long long
	vector <int> suf[31];

    long long countSubarrays(vector<int>& nums, int k) {
		ll ans=0;
		int n=nums.size();
		for(int i=0;i<30;i++) suf[i].resize(n+1);

		for(int i=n-1;i>=0;i--){
			for(int j=0;j<30;j++){
				if(i!=n-1 && (nums[i]&(1<<j))==(nums[i+1]&(1<<j))) suf[j][i]=suf[j][i+1];
				else suf[j][i]=i+1;
			}
		}

		for(int i=0;i<n;i++){
			int r=n;
            int l=i;
			for(int j=0;j<30;j++){
				if(k&(1<<j)){
                    if(nums[i]&(1<<j)) r=min(r,suf[j][i]);
                    else r=i;
                } 
                else{
                    if(nums[i]&(1<<j)){
                        l=max(l,suf[j][i]);
                    } 

                }

			}
            // cout<<l<<" "<<r<<endl;
			if(r>l) ans+=r-l;
		}
		return ans;
    }
};
