class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int sz = nums.size();
                long long fin = 0;

        unordered_map<long long, long long> mp;

                for (int i = 0; i < sz; ++i) {
                        unordered_map<long long, long long> mpp;

                        if (nums[i] == k) {
                fin++;

            }
                        mpp[nums[i]] = 1;

            for (auto it : mp) {

                                long long n_res = it.first & nums[i];
                                if (n_res == k) {
                                        fin += it.second;

                }
                                mpp[n_res] += it.second;

            }

            mp = mpp;

        }

        return fin;
    }
};
