class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt=0;
        int size = nums.size();

        unordered_map<long long, long long> prev;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> m; 

            if (nums[i] == k) {
                cnt++;
            }
            m[nums[i]] = 1;

            for (auto& [v, fre] : prev) {
                long long newAresulties = v & nums[i];
                if (newAresulties == k) {
                    cnt += fre;
                }
                m[newAresulties] += fre;
            }

            prev = m;
        }

        return cnt;

    }
};
