class Solution {
    public long countSubarrays(int[] nums, int desired) {
        int n = nums.length;
        long result = 0;
        Map<Long, Long> prevMap = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> currMap = new HashMap<>();
            if (nums[i] == desired) {
                ++result;
            }
            currMap.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : prevMap.entrySet()) {
                long key = entry.getKey();
                long val = entry.getValue();
                long newKey = key & nums[i];
                if (newKey == desired) {
                    result += val;
                }
                currMap.put(newKey, currMap.getOrDefault(newKey, 0L) + val);
            }

            prevMap = currMap;
        }

        return result;
    }
}
