import java.util.HashMap;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        long result = 0;     
        HashMap<Long, Long> map = new HashMap<>();

        for (int i = 0; i < nums.length; ++i) {
            HashMap<Long, Long> hMap = new HashMap<>();

            if (nums[i] == k) {
                ++result;
            }
            hMap.put((long) nums[i], 1L);

            for (HashMap.Entry<Long, Long> entry : map.entrySet()) {
                long tem = entry.getKey();
                long temp = entry.getValue();

                long last = tem & nums[i];
                if (last == k) {
                    result += temp;
                }
                hMap.put(last, hMap.getOrDefault(last, 0L) + temp);
            }

            map = hMap;
        }

        return result;
    }
}
