#include <bits/stdc++.h>
using namespace std;

class Solution
{
public:
#define ll long long
    long long countSubarrays(vector<int> &v, int k)
    {
        ll ans = 0;
        unordered_map<ll, ll> prev;

        for (int i = 0; i < v.size(); ++i)
        {
            unordered_map<ll, ll> curr;

            if (v[i] == k)
            {
                ans++;
            }
            curr[v[i]] = 1;
            for (auto &x : prev)
            {
                int val = x.first;
                int freq = x.second;
                ll result = val;
                result &= v[i];
                if (k == result)
                {
                    ans += freq;
                }
                curr[result] += freq;
            }

            prev = curr;
        }

        return ans;
    }
};
