int mod = 1e9+7;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = sz(nums);
        lli ans = 0;
        vector<int> c(32,0);

        for(int i=0;i<n;i++){
            int val = nums[i];

            for(int j=0;j<32;j++){
                if(val&(1<<j)){
                    c[j]++;
                }
                else{
                    c[j] = 0;
                }
            }

            int mini = i+1;
            int maxi = 0;
            for(int j=0;j<32;j++){
                if(k&(1<<j)){
                    mini = min(mini,c[j]);
                }
                else{
                    maxi = max(maxi,c[j]);
                }
            }
            ans += max(0,mini-maxi);
        }

        return ans;
    }
};
